rule.definition:
with(
  #"SYSTEM_SYSRULES_gridRowLayout"(
    contents: {
      #"SYSTEM_SYSRULES_floatingPointField"(

        value: ri!LocationDetails[ri!Index].Latitude,
        saveInto: ri!LocationDetails[ri!Index].Latitude,
        required:true
      ),
      #"SYSTEM_SYSRULES_floatingPointField"(

        value: ri!LocationDetails[ri!Index].Longitude,
        saveInto: {
          ri!LocationDetails[ri!Index].Longitude
        },
        required:true
      ),
      #"SYSTEM_SYSRULES_imageField"(
      images: {
        #"SYSTEM_SYSRULES_documentImage"(
          document: #"SYSTEM_SYSRULES_iconIndicator"(
            "REMOVE"
          ),
          caption: "Remove",
          link: #"SYSTEM_SYSRULES_dynamicLink"(
            value: {
              ri!Index
            },
            saveInto: {
              a!save(
                ri!LocationDetails,
                remove(
                  ri!LocationDetails,
                  save!value
                )
              )
            }
          )
        )
      },
      size: "ICON"
    )
    }
  )
)
