rule.definition:
load(
  local!pagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(
    startIndex: 1,
    batchSize: 20,
    sort: #"SYSTEM_SYSRULES_sortInfo"(
      field: "QuaterName",
      ascending: true
    )
  ),
  with(
     #"SYSTEM_SYSRULES_queryEntity"(
      entity: #"_a-0000ddf5-09b1-8000-9ccd-01ef9001ef90_41074",
      query: #"SYSTEM_SYSRULES_query"(
        aggregation: #"SYSTEM_SYSRULES_queryAggregation"(aggregationColumns: {
          #"SYSTEM_SYSRULES_queryAggregationColumn"(field: "QuaterName", isGrouping: true),
          #"SYSTEM_SYSRULES_queryAggregationColumn"(field: "QuaterCode", isGrouping: true),
          #"SYSTEM_SYSRULES_queryAggregationColumn"(field: "CommuneName", isGrouping: true),
          #"SYSTEM_SYSRULES_queryAggregationColumn"(field: "AddressId", isGrouping: true),
          #"SYSTEM_SYSRULES_queryAggregationColumn"(field: "CityId", isGrouping: true),
          #"SYSTEM_SYSRULES_queryAggregationColumn"(field: "Id", isGrouping: true),
          #"SYSTEM_SYSRULES_queryAggregationColumn"(field: "QuaterId", isGrouping: true),
          #"SYSTEM_SYSRULES_queryAggregationColumn"(field: "CommuneId", isGrouping: true),
          #"SYSTEM_SYSRULES_queryAggregationColumn"(field: "CommuneCode", isGrouping: true),
          #"SYSTEM_SYSRULES_queryAggregationColumn"(field: "CityCode", isGrouping: true),
          #"SYSTEM_SYSRULES_queryAggregationColumn"(field: "CityName", isGrouping: true),
          #"SYSTEM_SYSRULES_queryAggregationColumn"(field: "ProvinceName", isGrouping: true),
          #"SYSTEM_SYSRULES_queryAggregationColumn"(field: "ProvinceCode", isGrouping: true),
          #"SYSTEM_SYSRULES_queryAggregationColumn"(field: "ProvinceId", isGrouping: true),
        }),
       logicalExpression: if(
        and(
         #"_a-0000d33c-c5a1-8000-7bd7-760000760000_1541"(
            ri!ProvinceId
          ),
         #"_a-0000d33c-c5a1-8000-7bd7-760000760000_1541"(
            ri!CityId
          ),
          #"_a-0000d33c-c5a1-8000-7bd7-760000760000_1541"(
            ri!CommuneId
          )
        ),
        null,
        #"SYSTEM_SYSRULES_queryLogicalExpression"(
          operator: "OR",
          filters: {
        if(
          #"_a-0000d33c-c5a1-8000-7bd7-760000760000_1541"(
            ri!ProvinceId
          ),
          {},
          #"SYSTEM_SYSRULES_queryFilter"(
            field: "ProvinceId",
            operator: "=",
            value: ri!ProvinceId
          )
        ),
        if(
          #"_a-0000d33c-c5a1-8000-7bd7-760000760000_1541"(
            ri!CityId
          ),
          {},
          #"SYSTEM_SYSRULES_queryFilter"(
            field: "CityId",
            operator: "=",
            value: ri!CityId
          )
        ),
        if(
          #"_a-0000d33c-c5a1-8000-7bd7-760000760000_1541"(
            ri!CommuneId
          ),
          {},
          #"SYSTEM_SYSRULES_queryFilter"(
            field: "CommuneId",
            operator: "=",
            value: ri!CommuneId
          )
        )
        }
        )
        ),
         pagingInfo: local!pagingInfo
         )
         ).data
  )
)
